const RUNE_VALUE = {
    GOLD: function (rune) {
        return rune < 2 ? 0 : (3 + 2 * (rune - 2));
    },
    EPIC_FIND: function (rune) {
        return rune < 2 ? 0 : (3 + 2 * (rune - 2));
    },
    ITEM_QUALITY: function (rune) {
        switch (rune) {
            case 1: return 3;
            case 2: return 19;
            case 3: return 50;
            case 4: return 75;
            case 5: return 99;
            default: return 0;
        }
    },
    XP: function (rune) {
        switch (rune) {
            case 1: return 3;
            case 2: return 9;
            case 3: return 25;
            case 4: return 35;
            case 5: return 45;
            case 6: return 55;
            case 7: return 65;
            case 8: return 75;
            case 9: return 85;
            case 10: return 95;
            default: return 0;
        }
    },
    HEALTH: function (rune) {
        switch (rune) {
            case 1: return 3;
            case 2: return 6;
            case 3: return 17;
            case 4: return 23;
            case 5: return 30;
            case 6: return 36;
            case 7: return 43;
            case 8: return 50;
            case 9: return 56;
            case 10: return 64;
            case 11: return 72;
            case 12: return 80;
            case 13: return 88;
            case 14: return 94;
            case 15: return 99;
            default: return 0;
        }
    },
    SINGLE_RESISTANCE: function (rune) {
        if (rune < 2) {
            return 0;
        } else {
            return Math.floor((rune - 0.4) / 0.75);
        }
    },
    TOTAL_RESISTANCE: function (rune) {
        return RUNE_VALUE.SINGLE_RESISTANCE(rune * 3);
    },
    ELEMENTAL_DAMAGE: function (rune) {
        if (rune < 2) {
            return 0;
        } else {
            return Math.floor((rune - 0.3) / 0.6);
        }
    }
}

